/** 

=========================================================
* Vision UI PRO React - v1.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/vision-ui-dashboard-pro-react
* Copyright 2021 Creative Tim (https://www.creative-tim.com/)

* Design and Coded by Simmmple & Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Visionware.

*/

// react-router components
import { Link } from "react-router-dom";

// @mui material components
import Card from "@mui/material/Card";
import Divider from "@mui/material/Divider";
import Tooltip from "@mui/material/Tooltip";
import Grid from "@mui/material/Grid";

// Vision UI Dashboard PRO React components
import VuiBox from "components/VuiBox";
import VuiTypography from "components/VuiTypography";
import VuiButton from "components/VuiButton";

import { ReactComponent as XLauncherLogo } from "assets/images/logo.svg";

function EventCard({title, lockedTime, myXLH, apr, myRewards, stake, claim, reinvest, unstake}) {

  return (
    <Card sx={{ minHeight: "250px" }}>
      <VuiBox>
        <VuiBox display="flex" mb="12px" alignItems="center">
          <Tooltip key="logo" title="XLH" placement="bottom">
            <XLauncherLogo className='xlh-logo-stake'/>
          </Tooltip>
          <VuiBox ml="16px" display="flex" flexDirection="column" lineHeight={0}>
            <VuiTypography
              fontSize={16}
              fontWeight="medium"
              color="white"
              textTransform="capitalize"
            >
              {title}
            </VuiTypography>
            <VuiTypography
                variant="caption"
                fontWeight="regular"
                color="text"
                textTransform="capitalize"
              >
                {lockedTime}
            </VuiTypography>
          </VuiBox>
        </VuiBox>
        <VuiBox display="flex" justifyContent="space-between" alignItems="center">
          <VuiTypography component="span" fontSize={14} fontWeight="regular" color="text">
            APR:
          </VuiTypography>
          <VuiTypography fontSize={14} fontWeight="regular" color="white">
            {apr}
          </VuiTypography>
        </VuiBox>
        <VuiBox display="flex" justifyContent="space-between" alignItems="center">
          <VuiTypography component="span" fontSize={14} fontWeight="regular" color="text">
            My Staked XLH:
          </VuiTypography>
          <VuiTypography fontSize={14} fontWeight="regular" color="white">
            {myXLH}
          </VuiTypography>
        </VuiBox>
        <VuiBox display="flex" justifyContent="space-between" alignItems="center">
          <VuiTypography component="span" fontSize={14} fontWeight="regular" color="text">
            My Earned XLH:
          </VuiTypography>
          <VuiTypography fontSize={14} fontWeight="regular" color="white">
            {myRewards}
          </VuiTypography>
        </VuiBox>
        <Divider light />
        <Grid container spacing={1}>
          <Grid item xs={12} md={4} lg={4}>
            <VuiButton
              to={stake.route}
              color={stake.color}
              size={stake.size}
              sx={{ minWidth: "60px" }}
              onClick={stake.action}

            >
              {stake.label}
            </VuiButton>
          </Grid>
          <Grid item xs={12} md={4} lg={4}>
            <VuiButton
              to={claim.route}
              color={claim.color}
              size={claim.size}
              sx={{ minWidth: "60px" }}
              onClick={claim.action}

            >
              {claim.label}
            </VuiButton>
          </Grid>
          <Grid item xs={12} md={4} lg={4}>
            <VuiButton
              to={reinvest.route}
              color={reinvest.color}
              size={reinvest.size}
              sx={{ minWidth: "60px" }}
              onClick={reinvest.action}

            >
              {reinvest.label}
            </VuiButton>
          </Grid>
        </Grid>
      </VuiBox>
    </Card>
  );
}

export default EventCard;
